     public class Gato {
        private String nome, cor;
        private int idade;
        public Gato(String nome, String cor, int idade) {
            this.nome = nome;
            this.cor = cor;
            this.idade = idade;
        }
        public String getNome() {
            return nome;
        }
        public String getCor() {
            return cor;
        }
        public int getIdade() {
            return idade;
        }
        @Override
        public String toString() {
            return "Gato [nome=" + nome + ", cor=" + cor + ", idade=" + idade + "]";
        }

    }
